mapper.select:
select t.seq as adv_seq , t.channel_type , ( case when t.product_seq <> 0 then CONCAT(t.channel_type_name, ' 묶음 상품') else t.channel_type_name end ) as channel_type_name , t.title , t.start_date , t.end_date , t.audit_code , (select name from code where code = t.audit_code) as audit_code_name , t.product_seq , t.reg_date , t.progress_code , t.oddi_adv_cancel_date from (
) t where 1 = 1
and t.progress_code = 'PGT002' and t.audit_code = #{auditCode}
order by reg_date desc

select a.seq as adv_seq,p.seq as partner_seq, p.mall_name
	from adv a
 inner join adv_partner ap on a.seq = ap.adv_seq
 inner join partner p on ap.partner_seq = p.seq
where a.member_id = #{memberId}
  and a.seq = #{advSeq}

	and a.audit_code = #{auditCode}

select a.seq as adv_seq, pp.product_seq, p.seq as partner_seq, p.mall_name
	from adv a
inner join adv_partner ap on a.seq = ap.adv_seq
inner join product_partner pp on ap.partner_seq = pp.partner_seq
inner join partner p on pp.partner_seq = p.seq
where a.member_id = #{memberId}
  and a.seq = #{advSeq}
  and pp.product_seq  = #{productSeq}

	and a.audit_code = #{auditCode}

select count(1)
	from (

			)t
	where t.user_check = false
